mapper.update:
update ${prefix}ACT_HI_COMMENT set
REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}

where ROOT_PROC_INST_ID_ = #{rootProcessInstanceId, dbalType=VARCHAR}

update RES set
RES.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
FROM ${prefix}ACT_HI_COMMENT RES WITH (FORCESEEK)
where ROOT_PROC_INST_ID_ = #{rootProcessInstanceId, dbalType=VARCHAR}

update ${prefix}ACT_HI_COMMENT
  set REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
  where PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
    or TASK_ID_ in (
      SELECT ID_
      FROM ${prefix}ACT_HI_TASKINST
      WHERE PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
    )

update RES set
  RES.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
  FROM ${prefix}ACT_HI_COMMENT RES WITH (FORCESEEK)
  where RES.PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
    or RES.TASK_ID_ in (
      SELECT ID_
      FROM ${prefix}ACT_HI_TASKINST
      WHERE PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
    )

mapper.delete:
delete from ${prefix}ACT_HI_COMMENT where
TASK_ID_ in ( select ID_ from ${prefix}ACT_HI_TASKINST where
)
TASK_ID_ in ( select ID_ from ${prefix}ACT_HI_TASKINST where
)

mapper.select:
select * from ${prefix}ACT_HI_COMMENT 
where TASK_ID_ = #{taskId,dbalType=VARCHAR}
  and ID_ =  #{id,dbalType=VARCHAR}

select * from ${prefix}ACT_HI_COMMENT 
where TASK_ID_ = #{taskId,dbalType=VARCHAR}
  and ID_ =  #{id,dbalType=VARCHAR}
