mapper.update:
update ${prefix}ACT_RU_JOB set
  REV_ = 1 + REV_ ,
  SUSPENSION_STATE_ = #{parameter.suspensionState, dbalType=INTEGER}


    ID_ = #{parameter.jobId, dbalType=VARCHAR}


    and JOB_DEF_ID_ = #{parameter.jobDefinitionId, dbalType=VARCHAR}


    and PROCESS_INSTANCE_ID_ = #{parameter.processInstanceId, dbalType=VARCHAR}


    and PROCESS_DEF_ID_ = #{parameter.processDefinitionId, dbalType=VARCHAR}



      and PROCESS_DEF_KEY_ = #{parameter.processDefinitionKey, dbalType=VARCHAR}


      and PROCESS_DEF_ID_ IN (
        SELECT ID_ 
        FROM ${prefix}ACT_RE_PROCDEF PD
        WHERE PD.KEY_ = #{parameter.processDefinitionKey, dbalType=VARCHAR}

          and PD.TENANT_ID_ = #{parameter.processDefinitionTenantId, dbalType=VARCHAR}


          and PD.TENANT_ID_ is null

      )





    and HANDLER_TYPE_ =  #{parameter.handlerType, dbalType=VARCHAR}

update ${prefix}ACT_RU_JOB set REV_ = 1 + REV_ , LOCK_OWNER_ = NULL, LOCK_EXP_TIME_ = NULL, RETRIES_ = #{retries, dbalType=INTEGER}
RETRIES_ = 0
and JOB_DEF_ID_ = #{jobDefinitionId, dbalType=VARCHAR}


update ${prefix}ACT_RU_JOB set
  PRIORITY_ = #{priority, dbalType=INTEGER}
where
  JOB_DEF_ID_ = #{jobDefinitionId, dbalType=VARCHAR}

mapper.select:


${limitBefore}
select RES.ID_,
  RES.REV_,
  RES.DUEDATE_,
  RES.PROCESS_INSTANCE_ID_,
  RES.EXCLUSIVE_
${limitBetweenAcquisition}
from ${prefix}ACT_RU_JOB RES

where (RES.RETRIES_ > 0)
  and (

      RES.DUEDATE_ is null or

      RES.DUEDATE_ <= #{parameter.now, dbalType=VARCHAR}
  )
  and (RES.LOCK_OWNER_ is null or RES.LOCK_EXP_TIME_ < #{parameter.now, dbalType=VARCHAR})
  and RES.SUSPENSION_STATE_ = 1


    and (RES.DEPLOYMENT_ID_ is null

      or




    )



    and RES.PRIORITY_ >= #{parameter.jobPriorityMin}


    and RES.PRIORITY_ <= #{parameter.jobPriorityMax}


  and ()


    and HANDLER_TYPE_ != 'history-cleanup'



  ${orderBy}

${limitAfter}

select * from ${prefix}ACT_RU_JOB
where HANDLER_TYPE_ = #{parameter.handlerType}

  and TENANT_ID_ = #{parameter.tenantId}


  and TENANT_ID_ is null

and (HANDLER_CFG_ = #{parameter.handlerConfiguration, dbalType=VARCHAR}

or HANDLER_CFG_ = #{parameter.handlerConfigurationWithFollowUpJobCreatedProperty, dbalType=VARCHAR}
)

mapper.sql:
from ${prefix}ACT_RU_JOB RES

    LEFT OUTER JOIN ${prefix}ACT_RU_EXECUTION PI ON PI.ID_ = RES.PROCESS_INSTANCE_ID_


  LEFT OUTER JOIN ${prefix}ACT_RU_JOBDEF JD ON JD.ID_ = RES.JOB_DEF_ID_



    RES.ID_ = #{id}







    RES.JOB_DEF_ID_ = #{jobDefinitionId}


  	JD.ACT_ID_ = #{activityId}


    and RES.PROCESS_INSTANCE_ID_ = #{processInstanceId}







    and RES.EXECUTION_ID_ = #{executionId}


    and RES.PROCESS_DEF_ID_ = #{processDefinitionId}


    and RES.PROCESS_DEF_KEY_ = #{processDefinitionKey}


    and RES.RETRIES_ > 0


    and (RES.RETRIES_ > 0)
    and (RES.DUEDATE_ is null or RES.DUEDATE_ <= #{now, dbalType=VARCHAR})
    and (
        (RES.EXECUTION_ID_ is null)
        or
        (PI.SUSPENSION_STATE_ = 1 or PI.SUSPENSION_STATE_ is null)
  )


    and RES.TYPE_ = 'timer'


    and RES.TYPE_ = 'message'


    and RES.DUEDATE_ > #{duedateHigherThan}


    and RES.DUEDATE_ < #{duedateLowerThan}


    and RES.DUEDATE_ >= #{duedateHigherThanOrEqual}


    and RES.DUEDATE_ <= #{duedateLowerThanOrEqual}


    and RES.CREATE_TIME_ > #{createdAfter}


    and RES.CREATE_TIME_ <= #{createdBefore}


    and RES.PRIORITY_ >= #{priorityHigherThanOrEqual}


    and RES.PRIORITY_ <= #{priorityLowerThanOrEqual}


  	and (RES.EXCEPTION_MSG_ is not null or RES.EXCEPTION_STACK_ID_ is not null)


  	and RES.EXCEPTION_MSG_ = #{exceptionMessage}


    and RES.FAILED_ACT_ID_ = #{failedActivityId}


    and RES.RETRIES_ = 0


    and RES.SUSPENSION_STATE_ = #{suspensionState.stateCode}



      and ( RES.TENANT_ID_ in

        #{tenantId}


        or RES.TENANT_ID_ is null

      )


      and RES.TENANT_ID_ is null




    AND
      ( CASE

        WHEN
          RES.PROCESS_DEF_KEY_ is not null
        THEN
          (




          )

        ELSE

          1
      END ) = 1

