mapper.select:
SELECT RES.*, () ACT_INST_ID_ FROM ${prefix}ACT_RU_VARIABLE RES
LEFT JOIN ${prefix}ACT_RU_EXECUTION EXECUTION ON RES.EXECUTION_ID_ = EXECUTION.ID_
LEFT JOIN ${prefix}ACT_RU_EXECUTION PARENT_EXECUTION ON EXECUTION.PARENT_ID_ = PARENT_EXECUTION.ID_
WHERE TASK_ID_ = #{parameter.taskId, dbalType=VARCHAR}
AND NAME_ IN
#{item, dbalType=VARCHAR}

mapper.sql:
    from (
          select
            RES.*,
            () ACT_INST_ID_

          from ${prefix}ACT_RU_VARIABLE RES

          left join ${prefix}ACT_RU_EXECUTION EXECUTION
          on RES.EXECUTION_ID_ = EXECUTION.ID_

          left join ${prefix}ACT_RU_EXECUTION PARENT_EXECUTION
          on EXECUTION.PARENT_ID_ = PARENT_EXECUTION.ID_


            left join ${prefix}ACT_RU_EXECUTION PROC_EXECUTION
            on PROC_EXECUTION.ID_ = RES.PROC_INST_ID_

            left join ${prefix}ACT_RE_PROCDEF PROCDEF
            on PROCDEF.ID_ = PROC_EXECUTION.PROC_DEF_ID_   


              AUTH ON (AUTH.RESOURCE_ID_ ${authJoinStart}
                RES.PROC_INST_ID_ 
                ${authJoinSeparator} PROC_EXECUTION.ID_
                ${authJoinSeparator} PROCDEF.KEY_
                ${authJoinSeparator} RES.TASK_ID_
                ${authJoinSeparator} '*'
                ${authJoinEnd}
              )





              RES.ID_ = #{variableId}




              and RES.NAME_ = #{variableName}




              and RES.NAME_ in

                #{item}





              and RES.NAME_ like #{variableNameLike} ESCAPE ${escapeChar}




              and RES.EXECUTION_ID_ in

                #{item}





              and RES.PROC_INST_ID_ in

                #{item}





              and RES.CASE_EXECUTION_ID_ in

                #{item}





              and RES.CASE_INST_ID_ in

                #{item}





              and RES.TASK_ID_ in

                #{item}





              and RES.BATCH_ID_ in

                #{item}





              and RES.VAR_SCOPE_ in

                #{item}






              and (  IN

				#{id}

				  <when test="index != 0 and (index+1)%1000 == 0 and (index+1) < sizeOfIds">
					) or  IN (


				   ,


			    )



              and RES.TENANT_ID_ in

                #{tenantId}





              and




                and









    ) RES

mapper.update:
update ${prefix}ACT_GE_BYTEARRAY 
set
  REV_ = #{revisionNext, dbalType=INTEGER},
  BYTES_ = #{bytes, dbalType=BLOB}
where ID_ = #{id}
  and REV_ = #{revision, dbalType=INTEGER}

update ${prefix}ACT_GE_BYTEARRAY set
REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}

where ROOT_PROC_INST_ID_ = #{rootProcessInstanceId, dbalType=VARCHAR}

  update RES set
RES.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
FROM ${prefix}ACT_GE_BYTEARRAY RES WITH (FORCESEEK)
  where RES.ROOT_PROC_INST_ID_ = #{rootProcessInstanceId, dbalType=VARCHAR}

update ${prefix}ACT_GE_BYTEARRAY
  set REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
  where ID_ IN (

    SELECT BYTEARRAY_ID_
    FROM ${prefix}ACT_HI_VARINST
    WHERE PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
  )

update RES set
RES.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
FROM ${prefix}ACT_GE_BYTEARRAY RES WITH (FORCESEEK)
  where RES.ID_ IN (

    SELECT BYTEARRAY_ID_
    FROM ${prefix}ACT_HI_VARINST
    WHERE PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
  )

update ${prefix}ACT_GE_BYTEARRAY
  set REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
  where ID_ IN (

    SELECT BYTEARRAY_ID_
    FROM ${prefix}ACT_HI_DEC_IN I
    INNER JOIN ${prefix}ACT_HI_DECINST D
    ON I.DEC_INST_ID_ = D.ID_
    WHERE D.PROC_INST_ID_ = #{processInstanceId, dbalType=VARCHAR}
  )

update RES set
RES.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
