mapper.delete:


delete ${limitBeforeWithoutOffset} from ${prefix}ACT_GE_BYTEARRAY
where REMOVAL_TIME_ <= #{parameter.removalTime}

${limitAfterWithoutOffset}
OPTION (LOOP JOIN)

mapper.update:
update ${prefix}ACT_GE_BYTEARRAY
set
  REV_ = #{revisionNext, dbalType=INTEGER},
  BYTES_ = #{bytes, dbalType=BINARY}
where ID_ = #{id}
  and REV_ = #{revision, dbalType=INTEGER}

update ${prefix}ACT_GE_BYTEARRAY BA
  INNER JOIN ${prefix}ACT_HI_JOB_LOG JL
    ON BA.ID_ = JL.JOB_EXCEPTION_STACK_ID_
    AND JL.JOB_DEF_CONFIGURATION_ = #{batchId, dbalType=VARCHAR}
  SET BA.REMOVAL_TIME_ = #{removalTime, dbalType=VARCHAR}
